function setTheme(themeName) {
    "use strict";
    localStorage.setItem("theme", themeName);
    document.documentElement.className = themeName;
}
function toggleTheme() {
    "use strict";
    "theme-light" === localStorage.getItem("theme") ? setTheme("theme-dark") : setTheme("theme-light");
}
"theme-dark" === localStorage.getItem("theme") ? setTheme("theme-dark") : setTheme("theme-light");